import React from "react";

const wrap: React.CSSProperties = {
  position: "sticky",
  top: 0,
  zIndex: 50,
  backdropFilter: "blur(8px)",
  background: "rgba(10,16,28,0.35)",
  borderBottom: "1px solid rgba(148,163,184,0.08)",
};

const inner: React.CSSProperties = {
  maxWidth: 1200,
  margin: "0 auto",
  padding: "12px 32px",
  display: "flex",
  alignItems: "center",
  justifyContent: "space-between",
};

const brand: React.CSSProperties = {
  fontWeight: 900, color: "#f8fafc", letterSpacing: -0.2, fontSize: 16,
};

const nav: React.CSSProperties = { display: "flex", gap: 14 };
const link: React.CSSProperties = {
  color: "#cbd5e1",
  textDecoration: "none",
  padding: "6px 10px",
  borderRadius: 8,
  fontSize: 14,
  letterSpacing: 0.1,
};

export default function Navbar() {
  return (
    <div style={wrap}>
      <div style={inner}>
        <div style={brand}>Senluo Chen</div>
        <nav style={nav}>
            <a href="#about" style={link}>About</a>
  <a href="#projects" style={link}>Projects</a>
  <a href="#posts" style={link}>Posts</a>     {/* ← 新增 */}
  <a href="#contact" style={link}>Contact</a>
        </nav>
      </div>
    </div>
  );
}
