import React from "react";
import { Section, title, bar } from "./Section";

const field: React.CSSProperties = {
  width: "100%", padding: "12px 14px", borderRadius: 12,
  border: "1px solid rgba(148,163,184,0.25)", background: "#0b1220",
  color: "#e5e7eb", outline: "none", fontSize: 16, lineHeight: "24px", letterSpacing: 0.1,
};
const btn: React.CSSProperties = {
  width: "100%", padding: "12px 16px", borderRadius: 12, border: "none",
  background: "linear-gradient(90deg,#38bdf8,#0ea5e9)", color: "#06111d",
  fontWeight: 800, cursor: "pointer", letterSpacing: 0.3,
};

export default function Contact() {
  const onSubmit: React.FormEventHandler<HTMLFormElement> = (e) => {
    e.preventDefault();
    const d = new FormData(e.currentTarget);
    const body = encodeURIComponent(`Name: ${d.get("name")}\nEmail: ${d.get("email")}\n\n${d.get("message")}`);
    window.location.href = `mailto:hello@your-domain.com?subject=Portfolio%20Contact&body=${body}`;
  };

  return (
    <Section id="contact">
      <h2 style={title}>Contact</h2>
      <hr style={bar} />
      <form onSubmit={onSubmit} style={{ maxWidth: 560 }}>
        <input style={field} name="name" placeholder="Your name" required />
        <div style={{ height: 12 }} />
        <input style={field} name="email" type="email" placeholder="Your email" required />
        <div style={{ height: 12 }} />
        <textarea style={{ ...field, minHeight: 160, resize: "vertical" }} name="message" placeholder="Your message..." required />
        <div style={{ height: 14 }} />
        <button style={btn} type="submit">Send</button>
      </form>
    </Section>
  );
}
