import React from "react";
import ProjectCard from "./ProjectCard";
import { projects as raw } from "../data/projects";

const title: React.CSSProperties = {
  margin: "0 0 8px",
  color: "#f1f5f9",
  fontSize: 28,
  lineHeight: "36px",
  letterSpacing: -0.2,
  fontWeight: 800,
  textAlign: "left",
};

const subtitle: React.CSSProperties = {
  margin: "0 0 16px",
  color: "#94a3b8",
  fontSize: 16,
  lineHeight: "24px",
  textAlign: "left",
};

const bar: React.CSSProperties = {
  width: 88,
  height: 2,
  background: "rgba(148,163,184,0.35)",
  border: "none",
  margin: "0 0 32px 0",
};

const baseGrid: React.CSSProperties = {
  display: "grid",
  gap: 28,
  alignItems: "stretch",
};



// 卡片 hover 效果的 wrapper
const cardWrap: React.CSSProperties = {
  transition: "all 0.25s ease",
  borderRadius: 12,
  background: "rgba(255, 255, 255, 0.02)", // 背景讓卡片更立體
  padding: "20px", // 內距，避免文字緊貼邊框
  marginBottom: "12px", // 卡片與下一個之間留一點呼吸
};
const cardHover: React.CSSProperties = {
  transform: "translateY(-6px)",
  boxShadow: "0 8px 24px rgba(0,0,0,0.35)",
};

export default function Projects() {
  const projects = Array.isArray(raw) ? raw.filter(Boolean) : [];

  // 視窗小於 760px 時改為 1 欄，否則固定 2 欄
  const [oneCol, setOneCol] = React.useState(
    typeof window !== "undefined" ? window.innerWidth < 760 : false
  );
  React.useEffect(() => {
    const onResize = () => setOneCol(window.innerWidth < 760);
    window.addEventListener("resize", onResize);
    return () => window.removeEventListener("resize", onResize);
  }, []);

 const grid: React.CSSProperties = {
  display: "grid",
  gridTemplateColumns: "repeat(2, minmax(0, 1fr))", // 固定兩欄
  gap: 36, // 控制間距，比 28 大一點
  alignItems: "stretch",
};

  return (
    <section id="projects" aria-label="Projects">
      <h2 style={title}>Projects</h2>
      <p style={subtitle}>
        Selected work — product-focused builds with clean architecture and
        measurable impact.
      </p>
      <hr style={bar} />

      <div style={grid}>
        {projects.map((p) => (
          <div
            key={p.title}
            style={cardWrap}
            onMouseEnter={(e) =>
              Object.assign((e.currentTarget as HTMLElement).style, cardHover)
            }
            onMouseLeave={(e) =>
              Object.assign((e.currentTarget as HTMLElement).style, cardWrap)
            }
          >
            <ProjectCard project={p} />
          </div>
        ))}
      </div>
    </section>
  );
}
