import React from "react";
import ProjectCard from "./ProjectCard";
import { projects as raw } from "../data/projects";

const title: React.CSSProperties = {
  margin: "0 0 8px", color: "#f1f5f9", fontSize: 28, lineHeight: "36px",
  textAlign: "center", letterSpacing: -0.2, fontWeight: 800,
};
const bar: React.CSSProperties = { width: 88, height: 2, margin: "0 auto 24px", background: "rgba(148,163,184,0.35)", border: "none" };

const wrap: React.CSSProperties = { maxWidth: 1120, margin: "0 auto" };
const grid: React.CSSProperties = {
  display: "grid",
  gridTemplateColumns: "repeat(auto-fit, minmax(360px, 1fr))",
  gap: 22, // 卡片間距更鬆
  alignItems: "stretch",
};

export default function Projects() {
  const projects = Array.isArray(raw) ? raw.filter(Boolean) : [];
  return (
    <section id="projects" aria-label="Projects" style={wrap}>
      <h2 style={title}>Projects</h2>
      <hr style={bar} />
      <div style={grid}>
        {projects.map((p) => <ProjectCard key={p.title} project={p} />)}
      </div>
    </section>
  );
}
