import React from "react";

const section: React.CSSProperties = { paddingTop: 112 };

// 亮點圓球（可留可拿），改成靠左更一致
const orb: React.CSSProperties = {
  width: 112,
  height: 112,
  borderRadius: "50%",
  margin: "0 0 20px 0", // ← 不再置中
  background:
    "radial-gradient(circle at 30% 30%, #38bdf8 0%, #0ea5e9 45%, #0b1220 70%)",
  boxShadow: "0 22px 48px rgba(14,165,233,0.25)",
};

// H1 左對齊，字距微負值提升份量
const h1: React.CSSProperties = {
  fontSize: 56,
  lineHeight: "64px",
  fontWeight: 900,
  letterSpacing: -0.4,
  margin: "0 0 8px",
  color: "#f8fafc",
  textAlign: "left", // ← 左對齊
};

// 跟 About 一樣的左側細橫線
const bar: React.CSSProperties = {
  width: 88,
  height: 2,
  background: "rgba(148,163,184,0.35)",
  border: "none",
  margin: "0 0 18px 0", // ← 靠左
};

// 副標左對齊、舒適行高
const lead: React.CSSProperties = {
  margin: "0 0 20px 0",
  maxWidth: 860,
  fontSize: 18,
  lineHeight: "32px",
  color: "#b8c4d4",
  textAlign: "left", // ← 左對齊
};

// 下方內容全部靠左
const row: React.CSSProperties = {
  display: "flex",
  flexWrap: "wrap",
  gap: 10,
  justifyContent: "flex-start",
};

const pill: React.CSSProperties = {
  fontSize: 12,
  padding: "6px 10px",
  borderRadius: 999,
  color: "#e7edf5",
  background: "rgba(148,163,184,0.12)",
  border: "1px solid rgba(148,163,184,0.22)",
};

const ctas: React.CSSProperties = { ...row, gap: 12, marginTop: 16 };
const primary: React.CSSProperties = {
  padding: "12px 16px",
  borderRadius: 12,
  background: "linear-gradient(90deg,#38bdf8,#0ea5e9)",
  color: "#06111d",
  fontWeight: 800,
  border: "none",
  cursor: "pointer",
  letterSpacing: 0.2,
};
const ghost: React.CSSProperties = {
  padding: "12px 16px",
  borderRadius: 12,
  background: "transparent",
  color: "#e5eef8",
  border: "1px solid rgba(148,163,184,0.25)",
  cursor: "pointer",
  letterSpacing: 0.2,
};

export default function Hero() {
  return (
    <section aria-label="Intro" style={section}>
      <div style={orb} />
      {/* 不再用內部 box(maxWidth/margin auto)，直接讓 App.tsx 的 container 管寬度與對齊 */}
      <h1 style={h1}>Software Engineer · Full-Stack</h1>
      <hr style={bar} />
      <p style={lead}>
        I design and ship maintainable web apps — React/TypeScript on the front,
        Node/AWS on the back. Product-minded, performance-aware, focused on DX.
      </p>

      <div style={row}>
        {["React", "TypeScript", "Node", "AWS", "CI/CD", "DX"].map((t) => (
          <span key={t} style={pill}>
            {t}
          </span>
        ))}
      </div>

      <div style={ctas}>
        <a href="#projects" style={{ textDecoration: "none" }}>
          <button style={primary}>See Projects</button>
        </a>
        <a
          href="https://github.com/your-github"
          target="_blank"
          rel="noreferrer"
          style={{ textDecoration: "none" }}
        >
          <button style={ghost}>GitHub</button>
        </a>
      </div>
    </section>
  );
}
