import React from "react";

const h2: React.CSSProperties = { margin: "0 0 10px", color: "#f1f5f9", fontSize: 26, textAlign: "center" };
const bar: React.CSSProperties = { width: 80, height: 2, margin: "0 auto 18px", background: "rgba(148,163,184,0.35)", border: "none" };
const p: React.CSSProperties = { margin: "0 auto", color: "#cbd5e1", lineHeight: 1.8, maxWidth: 860, textAlign: "left" };

export default function About() {
  return (
    <section id="about" aria-label="About">
      <h2 style={h2}>About</h2>
      <hr style={bar} />
      <p style={p}>
        Engineer by training and product-driven by habit. I combine UI clarity with solid engineering:
        component systems, typed APIs, testing, and cloud deployments. I turn ideas into reliable, scalable software.
      </p>
    </section>
  );
}
