import React from "react";
import Navbar from "./components/Navbar";
import Hero from "./components/Hero";
import About from "./components/About";
import Projects from "./components/Projects";
import Contact from "./components/Contact";
import Footer from "./components/Footer";
import Posts from "./components/Posts";
import { motion, useReducedMotion } from "framer-motion";

// ---- Layout tokens ----------------------------------------------------------
const container: React.CSSProperties = {
  maxWidth: "min(1120px, 92vw)",
  margin: "0 auto",
  padding: "0 clamp(16px, 3vw, 32px)",
  display: "flex",
  flexDirection: "column",
  gap: "clamp(20px, 3.5vw, 28px)", // subtle default gap
};

const mainStyle: React.CSSProperties = {
  background: "linear-gradient(180deg, #0b1220 0%, #101a2d 60%, #0b1220 100%)",
  backgroundSize: "400% 400%",
  animation: "gradient 16s ease infinite",
  color: "#dbe2ea",
  minHeight: "100vh",
  paddingBottom: "96px",
  fontFamily:
    '"Inter","Noto Sans TC","PingFang TC","Microsoft JhengHei","Helvetica Neue",Arial,sans-serif',
  WebkitFontSmoothing: "antialiased",
  MozOsxFontSmoothing: "grayscale",
  fontSize: 16,
  lineHeight: 1.75,
  letterSpacing: 0.1,
};

// ---- Reusable section with consistent spacing/animation ---------------------
type SectionProps = React.PropsWithChildren<{
  delay?: number;
  id?: string;
  as?: keyof JSX.IntrinsicElements;
  padTop?: boolean;
  padBottom?: boolean;
}>;

function Section({
  children,
  delay = 0,
  id,
  as: Tag = "section",
  padTop = true,
  padBottom = true,
}: SectionProps) {
  const shouldReduceMotion = useReducedMotion();
  const paddingBlock = `clamp(28px, 6vw, 72px)`;
  return (
    <motion.div
      initial={shouldReduceMotion ? false : { opacity: 0, y: 32 }}
      whileInView={shouldReduceMotion ? {} : { opacity: 1, y: 0 }}
      transition={{ duration: 0.7, ease: "easeOut", delay }}
      viewport={{ once: true, margin: "0px 0px -80px" }}
      style={{
        paddingTop: padTop ? paddingBlock : 0,
        paddingBottom: padBottom ? paddingBlock : 0,
      }}
    >
      <Tag id={id} style={{ margin: 0 }}>{children}</Tag>
    </motion.div>
  );
}

export default function App() {
  return (
    <>
      <Navbar />
      <main style={mainStyle}>
        <div style={container}>
          {/* Hero gets a touch more top spacing, no bottom to pull next section closer */}
          <Section padBottom={false}>
            <Hero />
          </Section>

          <Section id="about" delay={0.05}>
            <About />
          </Section>

          <Section id="projects" delay={0.1}>
            <Projects />
          </Section>

          <Section id="posts" delay={0.15}>
            <Posts />
          </Section>

          <Section id="contact" delay={0.2}>
            <Contact />
          </Section>
        </div>
      </main>
      <Footer />
    </>
  );
}
