import React from "react";
import ProjectCard from "./ProjectCard";
import { projects as raw } from "../data/projects";

const wrap: React.CSSProperties = { maxWidth: 1120, margin: "0 auto" };

// Header：左邊標題 + 副標，右邊動作（GitHub）
const header: React.CSSProperties = {
  display: "flex",
  alignItems: "flex-end",
  justifyContent: "space-between",
  gap: 16,
  marginBottom: 16,
};

const title: React.CSSProperties = {
  margin: 0,
  color: "#f1f5f9",
  fontSize: 28,
  lineHeight: "36px",
  letterSpacing: -0.2,
  fontWeight: 800,
  textAlign: "left", // ← 改成靠左
};

const subtitle: React.CSSProperties = {
  marginTop: 6,
  color: "#9fb0c3",
  fontSize: 14,
  lineHeight: "22px",
  letterSpacing: 0.1,
  textAlign: "left",
};

const bar: React.CSSProperties = {
  width: 88,
  height: 2,
  background: "rgba(148,163,184,0.35)",
  border: "none",
  margin: "10px 0 18px 0", // ← 跟著靠左
};

const actionLink: React.CSSProperties = {
  textDecoration: "none",
  color: "#dbe2ea",
  fontSize: 14,
  border: "1px solid rgba(148,163,184,0.25)",
  padding: "8px 12px",
  borderRadius: 10,
  letterSpacing: 0.2,
  background: "rgba(2,6,23,0.25)",
};

const grid: React.CSSProperties = {
  display: "grid",
  gridTemplateColumns: "repeat(auto-fit, minmax(340px, 1fr))", // 更靈活
  gap: 24, // 卡片間距
  alignItems: "stretch",
};

export default function Projects() {
  const projects = Array.isArray(raw) ? raw.filter(Boolean) : [];
  return (
    <section id="projects" aria-label="Projects" style={wrap}>
      <div style={header}>
        <div>
          <h2 style={title}>Projects</h2>
          <p style={subtitle}>
            Selected work — product-focused builds with clean architecture and measurable impact.
          </p>
        </div>
        <a
          href="https://github.com/your-github"
          target="_blank"
          rel="noreferrer"
          style={actionLink}
        >
          View GitHub →
        </a>
      </div>

      <hr style={bar} />

      <div style={grid}>
        {projects.map((p) => (
          <ProjectCard key={p.title} project={p} />
        ))}
      </div>
    </section>
  );
}
