import React from "react";

export default function Footer() {
  return (
    <footer
      style={{
        borderTop: "1px solid rgba(148,163,184,0.12)",
        background: "linear-gradient(to right, #0b1628, #091321)", // 漸層更有層次
        color: "#9fb0c3",
        textAlign: "center",
        padding: "24px 20px", // 多一點呼吸感
        fontSize: 14,
        lineHeight: "22px",
        letterSpacing: 0.3,
      }}
    >
      <span style={{ color: "#f8fafc", fontWeight: 500 }}>
        © {new Date().getFullYear()} Senluo Chen
      </span>
      <span style={{ margin: "0 6px", color: "#64748b" }}>·</span>
      <span style={{ color: "#9fb0c3" }}>Paris</span>
    </footer>
  );
}
