import React from "react";
import ProjectCard from "./ProjectCard";
import { projects as raw } from "../data/projects";

const section: React.CSSProperties = {
  maxWidth: 1120,
  margin: "0 auto",
  padding: "0 20px", // 保持和 About 一致
};

const title: React.CSSProperties = {
  margin: "0 0 8px",
  color: "#f1f5f9",
  fontSize: 28,
  lineHeight: "36px",
  letterSpacing: -0.2,
  fontWeight: 800,
  textAlign: "left",
};

const subtitle: React.CSSProperties = {
  margin: "0 0 16px",
  color: "#94a3b8",
  fontSize: 16,
  lineHeight: "24px",
  textAlign: "left",
};

const bar: React.CSSProperties = {
  width: 88,
  height: 2,
  background: "rgba(148,163,184,0.35)",
  border: "none",
  margin: "0 0 32px 0", // 靠左
};

const grid: React.CSSProperties = {
  display: "grid",
  gridTemplateColumns: "repeat(auto-fit, minmax(360px, 1fr))",
  gap: 22,
  alignItems: "stretch",
};

export default function Projects() {
  const projects = Array.isArray(raw) ? raw.filter(Boolean) : [];
  return (
    <section id="projects" aria-label="Projects" style={section}>
      <h2 style={title}>Projects</h2>
      <p style={subtitle}>
        Selected work — product-focused builds with clean architecture and measurable impact.
      </p>
      <hr style={bar} />
      <div style={grid}>
        {projects.map((p) => (
          <ProjectCard key={p.title} project={p} />
        ))}
      </div>
    </section>
  );
}
