import React from "react";
import { Section } from "./Section";
import "../css/hero.css";
import "../css/section.css";

// ...existing code...

export default function Hero() {
  return (
    <Section className="hero-section">
  <div className="hero-orb" />
  <h1 className="hero-title">Software Engineer · Full-Stack</h1>
  <hr className="hero-bar" />
  <p className="hero-text">
      Passionate about AI and computer science, I create solutions to improve daily life and aim to found my own company. Interested in AI innovation? Let’s connect!
      </p>

      <div className="hero-row">
        {["React", "TypeScript", "Python", "AWS", "CI/CD", "DX"].map((t) => (
          <span key={t} className="hero-pill">{t}</span>
        ))}
      </div>

      <div className="hero-row" style={{ marginTop: 22 }}>
        <a href="https://github.com/SenluoChen" target="_blank" rel="noreferrer" style={{ textDecoration: "none" }}>
          <button className="hero-ghost-btn">GitHub</button>
        </a>
        <a href="#projects" style={{ textDecoration: "none" }}>
          <button className="hero-primary-btn">Behance</button>
        </a>
      </div>
    </Section>
  );
}
