import React from "react";

const h2: React.CSSProperties = {
  margin: "0 0 8px", color: "#f1f5f9", fontSize: 28, lineHeight: "36px",
  textAlign: "center", letterSpacing: -0.2, fontWeight: 800,
};
const bar: React.CSSProperties = { width: 88, height: 2, margin: "0 auto 20px", background: "rgba(148,163,184,0.35)", border: "none" };

const p: React.CSSProperties = {
  margin: "0 auto",
  maxWidth: 860,
  color: "#c7d3e0",
  fontSize: 17,
  lineHeight: "30px",
  textAlign: "left",
};

export default function About() {
  return (
    <section id="about" aria-label="About">
      <h2 style={h2}>About</h2>
      <hr style={bar} />
      <p style={p}>
        Engineer by training and product-driven by habit. I combine UI clarity with solid engineering:
        component systems, typed APIs, testing, and cloud deployments. My focus is turning ideas into
        reliable, scalable software.
      </p>
    </section>
  );
}
