import React, { useState } from "react";

const wrapper: React.CSSProperties = {
  display: "grid",
  gridTemplateColumns: "1fr 1fr",
  gap: 24,
  width: "100%",
  maxWidth: 1100,
  margin: "0 auto",
  padding: "24px 16px",
  boxSizing: "border-box",
};

const infoCard: React.CSSProperties = {
  borderRadius: 18,
  padding: 24,
  background:
    "linear-gradient(180deg, rgba(15,27,47,0.95) 0%, rgba(15,27,47,0.90) 100%)",
  border: "1px solid rgba(148,163,184,0.18)",
  boxShadow: "0 8px 22px rgba(0,0,0,0.35), inset 0 1px 0 rgba(255,255,255,0.04)",
  color: "#e5e7eb",
};

const list: React.CSSProperties = {
  listStyle: "none",
  margin: 0,
  padding: 0,
  display: "flex",
  flexDirection: "column",
  gap: 8,
};

const listItem: React.CSSProperties = {
  display: "flex",
  alignItems: "center",
  gap: 10,
  padding: "8px 0",
  color: "#e5e7eb",
};

const card: React.CSSProperties = {
  position: "relative",
  borderRadius: 18,
  padding: 28,
  textAlign: "left",
  height: "100%",
  display: "flex",
  flexDirection: "column",
  background:
    "linear-gradient(180deg, rgba(15,27,47,0.95) 0%, rgba(15,27,47,0.90) 100%), radial-gradient(120% 80% at 0% 0%, rgba(56,189,248,0.08) 0%, rgba(56,189,248,0) 70%)",
  border: "1px solid rgba(148,163,184,0.18)",
  boxShadow: "0 8px 22px rgba(0,0,0,0.35), inset 0 1px 0 rgba(255,255,255,0.04)",
  transition:
    "transform 200ms ease, box-shadow 200ms ease, border-color 200ms ease, background 200ms ease",
  maxWidth: "100%",
  boxSizing: "border-box",
  overflow: "hidden",
  minWidth: 0,
  color: "#e5e7eb",
};

const formStyle: React.CSSProperties = {
  width: "100%",
  maxWidth: "100%",
  margin: 0,
  display: "flex",
  flexDirection: "column",
  gap: 18,
  boxSizing: "border-box",
};

const labelStyle: React.CSSProperties = {
  display: "block",
  color: "#9fb0c3",
  fontSize: 13,
  marginBottom: 6,
};

const fieldBase: React.CSSProperties = {
  width: "100%",
  padding: "12px 14px",
  borderRadius: 14,
  border: "1px solid rgba(148,163,184,0.25)",
  background: "#0b1220",
  color: "#e5e7eb",
  outline: "none",
  fontSize: 16,
  lineHeight: "24px",
  letterSpacing: 0.1,
  boxSizing: "border-box",
  transition: "border-color 160ms ease, box-shadow 160ms ease",
};

const fieldFocus: React.CSSProperties = {
  borderColor: "rgba(56,189,248,0.6)",
  boxShadow: "0 0 0 3px rgba(56,189,248,0.15)",
};

const btn: React.CSSProperties = {
  width: "100%",
  padding: "12px 16px",
  borderRadius: 14,
  border: "none",
  background: "linear-gradient(90deg,#38bdf8,#0ea5e9)",
  color: "#06111d",
  fontWeight: 800,
  cursor: "pointer",
  letterSpacing: 0.3,
  fontSize: 16,
  transition: "transform 120ms ease, filter 120ms ease",
};

const btnHover: React.CSSProperties = {
  filter: "brightness(1.05)",
};

const btnActive: React.CSSProperties = {
  transform: "translateY(1px)",
  filter: "brightness(0.98)",
};

export default function Contact() {
  const [focusName, setFocusName] = useState(false);
  const [focusEmail, setFocusEmail] = useState(false);
  const [focusMsg, setFocusMsg] = useState(false);
  const [hover, setHover] = useState(false);
  const [active, setActive] = useState(false);

  return (
    <section id="contact" style={{ width: "100%" }}>
      <div style={wrapper}>
        {/* 左側：聯絡資訊 */}
        <div style={infoCard}>
          <h3 style={{ margin: "0 0 12px 0", fontSize: 18, color: "#cbd5e1" }}>
            Contact
          </h3>
          <ul style={list}>
            <li style={listItem}>
              <svg
                width="18"
                height="18"
                viewBox="0 0 24 24"
                fill="currentColor"
                style={{ opacity: 0.8 }}
                aria-hidden="true"
              >
                <path d="M6.62 10.79a15.053 15.053 0 006.59 6.59l2.2-2.2a1 1 0 011.11-.21c1.21.49 2.53.76 3.88.76a1 1 0 011 1v3.5a1 1 0 01-1 1C7.61 22 2 16.39 2 9.5a1 1 0 011-1H6.5a1 1 0 011 1c0 1.35.27 2.67.76 3.88a1 1 0 01-.21 1.11l-2.2 2.2z" />
              </svg>
              <a
                href="tel:+33614467463"
                style={{ color: "#e5e7eb", textDecoration: "none" }}
              >
                +33 06 14 46 74 63
              </a>
            </li>

            <li style={listItem}>
              <svg
                width="18"
                height="18"
                viewBox="0 0 24 24"
                fill="currentColor"
                style={{ opacity: 0.8 }}
                aria-hidden="true"
              >
                <path d="M12 2a7 7 0 00-7 7c0 5.25 7 13 7 13s7-7.75 7-13a7 7 0 00-7-7zm0 9.5A2.5 2.5 0 119.5 9 2.5 2.5 0 0112 11.5z" />
              </svg>
              <span>Based in Paris</span>
            </li>
          </ul>
        </div>

        {/* 右側：表單卡片 */}
        <div style={card}>
          <form
            className="contact-form"
            action="https://formspree.io/f/mpwlgdgo"
            method="POST"
            style={formStyle}
          >
            <div>
              <label style={labelStyle}>Name</label>
              <input
                style={{
                  ...fieldBase,
                  ...(focusName ? fieldFocus : {}),
                  fontWeight: 500,
                }}
                name="name"
                placeholder="Name"
                required
                onFocus={() => setFocusName(true)}
                onBlur={() => setFocusName(false)}
                aria-label="Your name"
              />
            </div>

            <div>
              <label style={labelStyle}>Email</label>
              <input
                style={{ ...fieldBase, ...(focusEmail ? fieldFocus : {}) }}
                name="email"
                type="email"
                placeholder="Email@example.com"
                required
                onFocus={() => setFocusEmail(true)}
                onBlur={() => setFocusEmail(false)}
                aria-label="Your email"
              />
            </div>

            <div>
              <label style={labelStyle}>Message</label>
              <textarea
                style={{
                  ...fieldBase,
                  ...(focusMsg ? fieldFocus : {}),
                  minHeight: 160,
                  resize: "vertical",
                  fontWeight: 500,
                }}
                name="message"
                placeholder="Let’s start a conversation!"
                required
                onFocus={() => setFocusMsg(true)}
                onBlur={() => setFocusMsg(false)}
                aria-label="Your message"
              />
            </div>

            <button
              type="submit"
              style={{
                ...btn,
                ...(hover ? btnHover : {}),
                ...(active ? btnActive : {}),
                marginTop: 6,
              }}
              onMouseEnter={() => setHover(true)}
              onMouseLeave={() => setHover(false)}
              onMouseDown={() => setActive(true)}
              onMouseUp={() => setActive(false)}
            >
              Send
            </button>

            <p
              style={{
                color: "#64748b",
                fontSize: 12,
                marginTop: 12,
                textAlign: "center",
              }}
            >
              Your message will be sent directly to my email.
            </p>
          </form>
        </div>
      </div>
    </section>
  );
}
