import React from "react";

const wrapBase: React.CSSProperties = {
  position: "sticky",
  top: 0,
  zIndex: 50,
  borderBottom: "1px solid rgba(148,163,184,0.08)",
  transition: "background 250ms ease, backdrop-filter 250ms ease, padding 250ms ease",
};

const inner: React.CSSProperties = {
  maxWidth: 1200,
  margin: "0 auto",
  padding: "20px 40px",
  display: "flex",
  alignItems: "center",
  justifyContent: "space-between",
};

const brandWrap: React.CSSProperties = {
  display: "flex",
  alignItems: "center",
  gap: 10,
};

const logo: React.CSSProperties = {
  width: 28,
  height: 28,
  borderRadius: "50%",
  background: "linear-gradient(135deg,#38bdf8,#0ea5e9)",
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  fontWeight: 900,
  fontSize: 16,
  color: "#0b1220",
};

const brand: React.CSSProperties = {
  fontWeight: 800,
  color: "#f8fafc",
  letterSpacing: -0.3,
  fontSize: 18,
  fontFamily: "'Space Grotesk','Inter','Helvetica Neue',sans-serif",
};

const nav: React.CSSProperties = { display: "flex", gap: 20 };
const link: React.CSSProperties = {
  color: "#cbd5e1",
  textDecoration: "none",
  padding: "8px 12px",
  borderRadius: 8,
  fontSize: 15,
  fontFamily: "'Inter','Helvetica Neue',sans-serif",
  letterSpacing: 0.2,
  transition: "color 0.2s ease, background 0.2s ease",
};

export default function Navbar() {
  const [scrolled, setScrolled] = React.useState(false);

  React.useEffect(() => {
    const onScroll = () => setScrolled(window.scrollY > 0);
    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  const wrap: React.CSSProperties = {
    ...wrapBase,
    background: scrolled
      ? "rgba(10,16,28,0.75)"
      : "#0b1220",
    backdropFilter: scrolled ? "blur(10px)" : "none",
    padding: scrolled ? "14px 40px" : "20px 40px", // 滾動縮小高度
  };

  return (
    <div style={wrap}>
      <div style={inner}>
        <div style={brandWrap}>
          <div style={logo}>S</div> {/* 可以換成 SVG Icon */}
          <div style={brand}>Senluo Chen</div>
        </div>
        <nav style={nav}>
          {["About", "Projects", "Posts", "Contact"].map((item) => (
            <a
              key={item}
              href={`#${item.toLowerCase()}`}
              style={{
                ...link,
                ...(scrolled
                  ? { color: "#e2e8f0" }
                  : { color: "#cbd5e1" }),
              }}
              onMouseEnter={(e) =>
                (e.currentTarget.style.color = "#38bdf8")
              }
              onMouseLeave={(e) =>
                (e.currentTarget.style.color = scrolled ? "#e2e8f0" : "#cbd5e1")
              }
            >
              {item}
            </a>
          ))}
        </nav>
      </div>
    </div>
  );
}
