import React from "react";
import type { Project } from "../data/projects";

type Props = { project: Project };

const card: React.CSSProperties = {
  position: "relative",
  background: "#0f1b2f",
  border: "1px solid rgba(148,163,184,0.16)",
  borderRadius: 16,
  padding: 22,
  boxShadow: "0 10px 26px rgba(0,0,0,0.28)",
  transition: "all 200ms ease",
  textAlign: "left",
  height: "100%",
  display: "flex",
  flexDirection: "column",
  cursor: "default",
};

const topBar: React.CSSProperties = {
  position: "absolute",
  left: 0,
  right: 0,
  top: 0,
  height: 3,
  borderTopLeftRadius: 16,
  borderTopRightRadius: 16,
  background: "linear-gradient(90deg,#38bdf8,#0ea5e9)",
  opacity: 0.9,
};

const h3: React.CSSProperties = {
  margin: "2px 0 6px",
  color: "#f6fbff",
  fontSize: 20,
  lineHeight: "28px",
  fontWeight: 800,
  letterSpacing: -0.2,
  transition: "color 0.2s ease",
};

const desc: React.CSSProperties = {
  margin: "8px 0 12px",
  color: "#b5c2d2",
  fontSize: 15,
  lineHeight: "26px",
};

const tagsRow: React.CSSProperties = {
  display: "flex",
  gap: 8,
  flexWrap: "wrap",
};
const tag: React.CSSProperties = {
  fontSize: 12,
  padding: "6px 10px",
  borderRadius: 999,
  background: "rgba(148,163,184,0.08)",
  border: "1px solid rgba(148,163,184,0.25)",
  color: "#e2e8f0",
  fontWeight: 500,
};

const actions: React.CSSProperties = {
  display: "flex",
  gap: 10,
  marginTop: "auto",
};
const btn: React.CSSProperties = {
  display: "inline-flex",
  alignItems: "center",
  gap: 8,
  padding: "10px 14px",
  borderRadius: 12,
  border: "1px solid rgba(148,163,184,0.25)",
  background: "rgba(2,6,23,0.25)",
  color: "#e5e7eb",
  textDecoration: "none",
  fontSize: 13,
  fontWeight: 700,
  letterSpacing: 0.2,
  transition: "all 0.2s ease",
};

export default function ProjectCard({ project: p }: Props) {
  const [hover, setHover] = React.useState(false);
  const primary = p.link || p.github;

  return (
    <article
      style={{
        ...card,
        transform: hover ? "translateY(-6px)" : "translateY(0)",
        boxShadow: hover
          ? "0 16px 34px rgba(0,0,0,0.36)"
          : card.boxShadow,
        borderColor: hover
          ? "rgba(56,189,248,0.45)"
          : (card.border as string),
        cursor: primary ? "pointer" : "default",
      }}
      onMouseEnter={() => setHover(true)}
      onMouseLeave={() => setHover(false)}
      onClick={() =>
        primary && window.open(primary, "_blank", "noopener,noreferrer")
      }
      onKeyDown={(e) =>
        e.key === "Enter" &&
        primary &&
        window.open(primary, "_blank")
      }
      tabIndex={0}
      aria-label={`${p.title} project`}
    >
      <div style={topBar} />
      <h3 style={{ ...h3, color: hover ? "#38bdf8" : h3.color }}>{p.title}</h3>
      <p style={desc}>{p.description}</p>
      <div style={tagsRow}>
        {p.tags.map((t) => (
          <span key={t} style={tag}>
            {t}
          </span>
        ))}
      </div>
      <div style={actions}>
        {p.link && (
          <a
            href={p.link}
            style={{
              ...btn,
              ...(hover && {
                background: "linear-gradient(90deg,#38bdf8,#0ea5e9)",
                color: "#fff",
                transform: "scale(1.05)",
              }),
            }}
            target="_blank"
            rel="noreferrer"
          >
            Live
          </a>
        )}
        {p.github && (
          <a
            href={p.github}
            style={{
              ...btn,
              ...(hover && {
                borderColor: "#38bdf8",
                transform: "scale(1.05)",
              }),
            }}
            target="_blank"
            rel="noreferrer"
          >
            GitHub
          </a>
        )}
      </div>
    </article>
  );
}
