import React from "react";
import { Section } from "./Section";
import "../css/hero.css"; // 下面有新加的 .contact-* 樣式

export default function Contact() {
  const [hover, setHover] = React.useState(false);
  const [active, setActive] = React.useState(false);
  const [focus, setFocus] = React.useState<{name?: boolean; email?: boolean; msg?: boolean}>({});

  const onSubmit: React.FormEventHandler<HTMLFormElement> = (e) => {
    e.preventDefault();
    const d = new FormData(e.currentTarget);
    const body = encodeURIComponent(
      `Name: ${d.get("name")}\nEmail: ${d.get("email")}\n\n${d.get("message")}`
    );
    window.location.href = `mailto:hello@your-domain.com?subject=Portfolio%20Contact&body=${body}`;
  };

  return (
    <Section id="contact" aria-labelledby="contact-heading">
      <header className="contact-header">
        <h2 id="contact-heading" className="contact-title">Contact</h2>
        <p className="contact-subtitle">
          Have a project, collaboration idea, or just want to say hello? I usually reply within{" "}
          <strong>24–48h</strong>.
        </p>
        <hr className="contact-bar" />
      </header>

      <div className="contact-grid">
        {/* 左：資訊卡 */}
        <aside className="contact-card">
          <ul className="contact-list" role="list">
            <li className="contact-item">
              <svg width="18" height="18" viewBox="0 0 24 24" aria-hidden>
                <path fill="currentColor" d="M12 12.713L1.2 6h21.6L12 12.713zM12 14.9L22.8 8v10.8H1.2V8L12 14.9z" />
              </svg>
              <a href="mailto:hello@your-domain.com" className="contact-link">hello@your-domain.com</a>
            </li>
            <li className="contact-item">
              <svg width="18" height="18" viewBox="0 0 24 24" aria-hidden>
                <path fill="currentColor" d="M19 3H5a2 2 0 00-2 2v14l4-4h12a2 2 0 002-2V5a2 2 0 00-2-2z" />
              </svg>
              <span>Based in Paris · Available for collaboration</span>
            </li>
            <li className="contact-item">
              <svg width="18" height="18" viewBox="0 0 24 24" aria-hidden>
                <path fill="currentColor" d="M12 2a7 7 0 00-7 7c0 5.25 7 13 7 13s7-7.75 7-13a7 7 0 00-7-7zm0 9.5A2.5 2.5 0 119.5 9 2.5 2.5 0 0112 11.5z" />
              </svg>
              <span>Europe/Paris (CET/CEST)</span>
            </li>
          </ul>
        </aside>

        {/* 右：表單卡 */}
        <div className="contact-card">
          <form onSubmit={onSubmit} noValidate>
            <label className="contact-label" htmlFor="name">Name</label>
            <input
              id="name" name="name" placeholder="Your name" required
              className={`contact-field ${focus.name ? "is-focus" : ""}`}
              onFocus={() => setFocus(s => ({...s, name: true}))}
              onBlur={() => setFocus(s => ({...s, name: false}))}
            />

            <label className="contact-label" htmlFor="email">Email</label>
            <input
              id="email" name="email" type="email" placeholder="your@email.com" required
              className={`contact-field ${focus.email ? "is-focus" : ""}`}
              onFocus={() => setFocus(s => ({...s, email: true}))}
              onBlur={() => setFocus(s => ({...s, email: false}))}
            />

            <label className="contact-label" htmlFor="message">Message</label>
            <textarea
              id="message" name="message" placeholder="Tell me a bit about your idea or project..."
              required className={`contact-field contact-textarea ${focus.msg ? "is-focus" : ""}`}
              onFocus={() => setFocus(s => ({...s, msg: true}))}
              onBlur={() => setFocus(s => ({...s, msg: false}))}
            />

            <button
              type="submit"
              className={`contact-btn ${hover ? "is-hover" : ""} ${active ? "is-active" : ""}`}
              onMouseEnter={() => setHover(true)}
              onMouseLeave={() => setHover(false)}
              onMouseDown={() => setActive(true)}
              onMouseUp={() => setActive(false)}
            >
              Send
            </button>

            <p className="contact-hint">This form opens your email app with a pre-filled message.</p>
          </form>
        </div>
      </div>
    </Section>
  );
}
