import React from "react";

const h2: React.CSSProperties = { margin: "0 0 10px", color: "#f1f5f9", fontSize: 26, textAlign: "center" };
const bar: React.CSSProperties = { width: 80, height: 2, margin: "0 auto 18px", background: "rgba(148,163,184,0.35)", border: "none" };

const field: React.CSSProperties = {
  width: "100%", padding: "12px 14px", borderRadius: 10,
  border: "1px solid rgba(148,163,184,0.25)", background: "#0b1220", color: "#e5e7eb", outline: "none",
};
const btn: React.CSSProperties = {
  width: "100%", padding: "12px 14px", borderRadius: 10, border: "none",
  background: "linear-gradient(90deg,#38bdf8,#0ea5e9)", color: "#0b1220", fontWeight: 800, cursor: "pointer",
};

export default function Contact() {
  const onSubmit: React.FormEventHandler<HTMLFormElement> = (e) => {
    e.preventDefault();
    const d = new FormData(e.currentTarget);
    const body = encodeURIComponent(`Name: ${d.get("name")}\nEmail: ${d.get("email")}\n\n${d.get("message")}`);
    window.location.href = `mailto:hello@your-domain.com?subject=Portfolio%20Contact&body=${body}`;
  };

  return (
    <section id="contact" aria-label="Contact" style={{ maxWidth: 560, margin: "0 auto" }}>
      <h2 style={h2}>Contact</h2>
      <hr style={bar} />
      <form onSubmit={onSubmit} style={{ textAlign: "left" }}>
        <input style={field} name="name" placeholder="Your name" required />
        <div style={{ height: 10 }} />
        <input style={field} name="email" type="email" placeholder="Your email" required />
        <div style={{ height: 10 }} />
        <textarea style={{ ...field, minHeight: 140, resize: "vertical" }} name="message" placeholder="Your message..." required />
        <div style={{ height: 12 }} />
        <button style={btn} type="submit">Send</button>
      </form>
    </section>
  );
}
